import React from "react";
import { Form, Label, Input, FormGroup, Button } from "reactstrap";
import axios from "axios";

function CreatePost() {
  const submitHandler = (event) => {
    event.preventDefault();
    const formData = new FormData();
    formData.append("posts", JSON.stringify({
      tittle: event.target.tittle.value,
      description: event.target.description.value
    }));
    formData.append("eventImage", event.target.eventImage.files[0]);
    formData.append("qrImage", event.target.qrImage.files[0]);

    axios
      .post("http://localhost:3000/posts", formData, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
      })
      .then((response) => {
        console.log(response);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  return (
    <>
      <Form
        style={{ width: "900px", marginTop: "50px", marginLeft: "280px" }}
        onSubmit={submitHandler}
      >
        <FormGroup>
          <Label for="tittle">Title</Label>
          <Input
            id="tittle"
            name="tittle"
            placeholder="Enter your title"
            type="input"
          />
        </FormGroup>
        <FormGroup>
          <Label for="description">Description</Label>
          <Input
            id="description"
            placeholder="Write your description"
            name="description"
            type="textarea"
          />
        </FormGroup>
        <FormGroup>
          <Label for="eventImage">Upload Event Image</Label>
          <Input id="eventImage" name="eventImage" type="file" />
        </FormGroup>
        <FormGroup>
          <Label for="qrImage">Upload QR Image</Label>
          <Input id="qrImage" name="qrImage" type="file" />
        </FormGroup>
        <Button color="success" type="submit">
          Submit
        </Button>
      </Form>
    </>
  );
}

export default CreatePost;
