import React, { useState } from "react";
import { Form, Label, Input, FormGroup, Button } from "reactstrap";
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

function CreatePost() {
  const [formData, setFormData] = useState({
    title: "",
    description: "",
    eventImage: null,
    qrImage: null
  });

  const handleChange = (e) => {
    const { name, value, files } = e.target;

    if (files) {
      const file = files[0];
      const reader = new FileReader();

      reader.onloadend = () => {
        const byteArray = new Uint8Array(reader.result);
        setFormData(prevState => ({
          ...prevState,
          [name]: byteArray
        }));
      };

      reader.readAsArrayBuffer(file);
    } else {
      setFormData(prevState => ({
        ...prevState,
        [name]: value
      }));
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const formDataJSON = {
      title: formData.title,
      description: formData.description,
      eventImage: formData.eventImage,
      qrImage: formData.qrImage
    };

    fetch('http://localhost:3000/posts', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(formDataJSON)
      
    })
    .then(response => {
      if (response.ok) {
        toast.success('Post created successfully');
        // handle success, maybe redirect or show a success message
      } else {
        throw new Error('Failed to create post');
      }
    })
    .catch(error => {
      const jsonobj = JSON.stringify(formDataJSON)
      console.log(jsonobj)
      toast.error('Failed to create post');
      console.log(error)

      // handle network errors
    });
  };

  return (
    <>
      <ToastContainer />
      <Form style={{ width: "900px" , marginTop: "50px", marginLeft: "280px" }} onSubmit={handleSubmit}>
        <FormGroup>
          <Label for="title">Title</Label>
          <Input id="title" name="title" placeholder="Enter your title" type="input" onChange={handleChange}/>
        </FormGroup>
        <FormGroup>
          <Label for="description">Description</Label>
          <Input id="description" placeholder="Write your description" name="description" type="textarea" onChange={handleChange}/>
        </FormGroup>
        <FormGroup>
          <Label for="eventImage">Upload Event Image</Label>
          <Input id="eventImage" name="eventImage" type="file" onChange={handleChange}/>
          <Label for="qrImage">Upload QR Image</Label>
          <Input id="qrImage" name="qrImage" type="file" onChange={handleChange}/>  
        </FormGroup>
        <Button color="success" type="submit">Submit</Button>
      </Form>
    </>
  );
}

export default CreatePost;
