import React from "react";
import { Form, Label, Input, FormGroup, Button } from "reactstrap";

function CreatePost() {

  const submitHandler = (Event) =>{
    Event.preventDefault()
    const tittle = Event.target
  }

  return (
    <>
      <Form style={{ width: "900px" , marginTop: "50px", marginLeft: "280px" }}  onSubmit={submitHandler}>
        <FormGroup>
          <Label for="tittle">Title</Label>
          <Input id="tittle" name="tittle" placeholder="enter your tittle" type="input"/>
        </FormGroup>
        <FormGroup>
          <Label for="discription">Discription</Label>
          <Input id="discription" placeholder="write your description" name="discription" type="textarea"/>
        </FormGroup>
        <FormGroup>
        <Label for="eventImage">upload Event Image</Label>
        <Input id="eventImage" name="eventImage"type="file"/>
        <Label for="qrImage">upload qr Image</Label>
        <Input id="qrImage" name="qrImage"type="file"/>  
       </FormGroup>
        <Button color="success"> Submit </Button>
      </Form>
    </>
  );
}

export default CreatePost;
