import React, { useEffect, useState } from 'react';
import Header from "./Header";
import PublicPost from "./PublicPost";
import { Button } from 'reactstrap';
import admin from './Admin.JPG'

function Allpost() {

  const [publicPosts, setPublicPosts] = useState([]);
  
  useEffect(() => {
    fetch(`http://localhost:3000/posts`)
      .then((response) => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then((data) => {
        setPublicPosts(data); 
      })
      .catch((error) => {
        console.error('Error fetching data:', error);
      });
  }); 

  return (
    <div className="home-container">
      <Header />

      <Button className='adminLogin'>  
      Admin login
      <img  src={admin}  style={{height:'30px', width:'30px', position:'relative', left:'0px', borderRadius: '50' }} alt="Admin logo" />
      </Button>

      <br />
      {publicPosts.map((publicPost) => (
        <PublicPost
          key={publicPost.id}
          id={publicPost.id}
          title={publicPost.title}
          description={publicPost.description}
          timeStamp={publicPost.timeStamp}
          eventImage={publicPost.eventImage}
          qrImage={publicPost.qrImage}
        />
      ))}
      <br />
      <br />
    </div>
  );
}

export default Allpost;
