import React, { useState } from "react";
import { Form, Label, Input, FormGroup, Button } from "reactstrap";
import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

function CreatePost() {
  const [formData, setFormData] = useState({
    title: "",
    description: "",
    eventImage: null,
    qrImage: null
  });

  const handleChange = (e) => {
    const { name, value, files } = e.target;

    if (files) {
      const file = files[0];
      setFormData(prevState => ({
        ...prevState,
        [name]: file
      }));
    } else {
      setFormData(prevState => ({
        ...prevState,
        [name]: value
      }));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const formDataJSON = new FormData();
      formDataJSON.append("title", formData.title);
      formDataJSON.append("description", formData.description);
      formDataJSON.append("eventImage", formData.eventImage);
      formDataJSON.append("qrImage", formData.qrImage);

      const response = await fetch('http://localhost:3000/posts', {
        method: 'POST',
        body: formDataJSON
      });

      if (response.ok) {
        toast.success('Post created successfully');
        // Optionally, you can reset the form after successful submission
        setFormData({
          title: "",
          description: "",
          eventImage: null,
          qrImage: null
        });
      } else {
        throw new Error('Failed to create post');
      }
    } catch (error) {
      toast.error('Failed to create post');
    }
  };

  return (
    <>
      <Form style={{ width: "900px" , marginTop: "50px", marginLeft: "280px" }} onSubmit={handleSubmit}>
        <FormGroup>
          <Label for="title">Title</Label>
          <Input id="title" name="title" placeholder="Enter your title" type="input" value={formData.title} onChange={handleChange}/>
        </FormGroup>
        <FormGroup>
          <Label for="description">Description</Label>
          <Input id="description" placeholder="Write your description" name="description" type="textarea" value={formData.description} onChange={handleChange}/>
        </FormGroup>
        <FormGroup>
          <Label for="eventImage">Upload Event Image</Label>
          <Input id="eventImage" name="eventImage" type="file" onChange={handleChange}/>
          <Label for="qrImage">Upload QR Image</Label>
          <Input id="qrImage" name="qrImage" type="file" onChange={handleChange}/>  
        </FormGroup>
        <Button color="success" type="submit">Submit</Button>
      </Form>
    </>
  );
}

export default CreatePost;
