import React from "react";
import { Form, Label, Input, FormGroup, Button } from "reactstrap";
import axios from "axios";

function CreatePost() {
  const submitHandler = (Event) => {
    Event.preventDefault();
    const tittle = Event.target.tittle.value;
    const description = Event.target.description.value;
    const eventImageArray = new Uint8Array(Event.target.eventImage.value);
    const eventImage = null;
    const qrImage = null;

    var fileInput = document.getElementById("fileInput");

    fileInput.addEventListener("change", function (event) {
      var file = event.target.files[0];

      var reader = new FileReader();

      reader.onload = function (event) {
      
        console.log(byteArray); // Here is your byte array
      };

      reader.readAsArrayBuffer(file);
    });

    axios
      .post("http://localhost:3000/posts", {
        tittle,
        description,
        eventImage,
        qrImage,
      })
      .then((response) => {
        console.log(response);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  return (
    <>
      <Form
        style={{ width: "900px", marginTop: "50px", marginLeft: "280px" }}
        onSubmit={submitHandler}
      >
        <FormGroup>
          <Label for="tittle">Title</Label>
          <Input
            id="tittle"
            name="tittle"
            placeholder="Enter your title"
            type="input"
          />
        </FormGroup>
        <FormGroup>
          <Label for="description">Description</Label>
          <Input
            id="description"
            placeholder="Write your description"
            name="description"
            type="textarea"
          />
        </FormGroup>
        <FormGroup>
          <Label for="eventImage">Upload Event Image</Label>
          <Input id="eventImage" name="eventImage" type="file" />
          <Label for="qrImage">Upload QR Image</Label>
          <Input id="qrImage" name="qrImage" type="file" />
        </FormGroup>
        <Button color="success" type="submit">
          Submit
        </Button>
      </Form>
    </>
  );
}

export default CreatePost;
