import React, { useState } from "react";
import { Form, Label, Input, FormGroup, Button } from "reactstrap";
import axios from "axios";

function CreatePost() {
  const [formData, setFormData] = useState({
    title: "",
    description: "",
    eventImage: null,
    qrImage: null
  });

  const handleChange = (event) => {
    setFormData({
      ...formData,
      [event.target.name]: event.target.value
    });
  };

  const handleFileChange = (event) => {
    setFormData({
      ...formData,
      [event.target.name]: event.target.files[0]
    });
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    const postData = new FormData();
    postData.append("title", formData.title);
    postData.append("description", formData.description);
    postData.append("eventImage", formData.eventImage);
    postData.append("qrImage", formData.qrImage);

    try {
      const response = await axios.post('http://localhost:3000/posts', postData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });
      console.log(response);
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <Form style={{ width: "900px", marginTop: "50px", marginLeft: "280px" }} onSubmit={handleSubmit}>
      <FormGroup>
        <Label for="title">Title</Label>
        <Input id="title" name="title" placeholder="Enter your title" type="input" value={formData.title} onChange={handleChange} />
      </FormGroup>
      <FormGroup>
        <Label for="description">Description</Label>
        <Input id="description" placeholder="Write your description" name="description" type="textarea" value={formData.description} onChange={handleChange} />
      </FormGroup>
      <FormGroup>
        <Label for="eventImage">Upload Event Image</Label>
        <Input id="eventImage" name="eventImage" type="file" onChange={handleFileChange} />
        <Label for="qrImage">Upload QR Image</Label>
        <Input id="qrImage" name="qrImage" type="file" onChange={handleFileChange} />
      </FormGroup>
      <Button color="success" type="submit">Submit</Button>
    </Form>
  );
}

export default CreatePost;
