import React, { useState, useEffect } from 'react';

function Filter() {
  const [selectedOption, setSelectedOption] = useState('');

  useEffect(() => {
    if (selectedOption === '') {
      // Call the first API if selectedOption is null
      fetch('http://localhost:3000/posts')
        .then((response) => {
          if (!response.ok) {
            throw new Error('Network response was not ok');
          }
          return response.json();
        })
        .then((data) => {
          console.log(data);
          // Handle the data from the first API call
        })
        .catch((error) => {
          console.error('Error fetching data:', error);
        });
    } else {
      // Call the second API if selectedOption has some value
      fetch(`http://localhost:3000/posts/branch/${selectedOption}`)
        .then((response) => {
          if (!response.ok) {
            throw new Error('Network response was not ok');
          }
          return response.json();
        })
        .then((data) => {
          console.log(data);
          // Handle the data from the second API call
        })
        .catch((error) => {
          console.error('Error fetching data:', error);
        });
    }
  }, [selectedOption]);

  const handleCheckboxChange = (event) => {
    setSelectedOption(event.target.value);
  };

  return (
    <form>
      <div style={{ display: 'flex', flexDirection: 'row' }}>
        <input
          type="checkbox"
          id="branch1"
          name="branch1"
          value="IT"
          checked={selectedOption === 'IT'}
          onChange={handleCheckboxChange}
        />
        <label htmlFor="branch1"> IT</label>
      </div>
      <div style={{ display: 'flex', flexDirection: 'row' }}>
        <input
          type="checkbox"
          id="branch2"
          name="branch2"
          value="CE"
          checked={selectedOption === 'CE'}
          onChange={handleCheckboxChange}
        />
        <label htmlFor="branch2"> CE</label>
      </div>
      <br />
      <input type="submit" value="Submit" />
    </form>
  );
}

export default Filter;
